// src/components/Game.jsx
import React, { useState } from 'react';

const choices = [
  { name: 'Rock', emoji: '🪨' },
  { name: 'Paper', emoji: '📄' },
  { name: 'Scissors', emoji: '✂️' },
];

function getResult(player, computer) {
  if (player === computer) return 'Draw 😐';
  if (
    (player === 'Rock' && computer === 'Scissors') ||
    (player === 'Paper' && computer === 'Rock') ||
    (player === 'Scissors' && computer === 'Paper')
  ) return 'Win 🎉';
  return 'Lose 😢';
}

export default function Game() {
  const [playerChoice, setPlayerChoice] = useState(null);
  const [computerChoice, setComputerChoice] = useState(null);
  const [result, setResult] = useState('');
  const [playerScore, setPlayerScore] = useState(0);
  const [computerScore, setComputerScore] = useState(0);

  const handleChoice = (choice) => {
    const computer = choices[Math.floor(Math.random() * choices.length)].name;
    const outcome = getResult(choice, computer);
    setPlayerChoice(choice);
    setComputerChoice(computer);
    setResult(outcome);

    if (outcome === 'Win 🎉') setPlayerScore(s => s + 1);
    if (outcome === 'Lose 😢') setComputerScore(s => s + 1);
  };

  const resetRound = () => {
    setPlayerChoice(null);
    setComputerChoice(null);
    setResult('');
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-indigo-500 to-purple-600">
      <div className="bg-white/10 backdrop-blur-md rounded-xl shadow-xl p-8 w-full max-w-md flex flex-col items-center">
        <h1 className="text-4xl font-bold text-white mb-8 text-center drop-shadow-lg">
          Rock Paper Scissors
        </h1>
        <div className="flex gap-6 mb-6">
          {choices.map(({ name, emoji }) => (
            <button
              key={name}
              className="bg-white rounded-full px-6 py-4 text-2xl font-bold shadow-lg transition-all hover:scale-105 hover:bg-indigo-100 focus:outline-none"
              onClick={() => handleChoice(name)}
              disabled={!!result}
            >
              {emoji}
            </button>
          ))}
        </div>
        <div className="flex justify-between w-full mb-4 px-2">
          <div className="text-lg font-semibold text-indigo-100">
            Player: <span className="font-bold text-white">{playerScore}</span>
          </div>
          <div className="text-lg font-semibold text-purple-100">
            Computer: <span className="font-bold text-white">{computerScore}</span>
          </div>
        </div>
        {result && (
          <div className={`mt-4 text-2xl font-bold ${
            result === 'Win 🎉'
              ? 'text-green-400'
              : result === 'Lose 😢'
              ? 'text-red-400'
              : 'text-yellow-300'
          } drop-shadow-lg`}>
            {result}
          </div>
        )}
        {playerChoice && computerChoice && (
          <div className="flex gap-8 mt-4">
            <div className="flex flex-col items-center">
              <span className="text-3xl">{choices.find(c => c.name === playerChoice).emoji}</span>
              <span className="text-lg text-indigo-200">You</span>
            </div>
            <div className="flex flex-col items-center">
              <span className="text-3xl">{choices.find(c => c.name === computerChoice).emoji}</span>
              <span className="text-lg text-purple-200">Computer</span>
            </div>
          </div>
        )}
        <button
          className="mt-8 bg-indigo-500 text-white px-4 py-2 rounded-lg font-semibold hover:bg-indigo-600 transition-all"
          onClick={resetRound}
          disabled={!result}
        >
          Play Again
        </button>
      </div>
    </div>
  );
}
